import './App.css'
import {useState} from 'react'
import {Todo} from './components/Todo'
import {AddTodo} from './components/AddTodo'

function App() {
  const [todo, setTodo] = useState('')
  const [todoList, setTodoList] = useState([])
  const addTodo = () => {
      if(todo.trim().length !== 0 && !todoList.includes(todo)){
        setTodoList([...todoList, todo])
      }
  }
  const clearAllTodos = () => {
    setTodoList([])
  }
  return (
    <div className="App">
        <h1 className="todo-title">
          <span>TODO App</span>
        </h1>
        <AddTodo />
        <div className="todos-list">
          {
            todoList.length >0 ? 
            (<ul>
              {
                todoList.map((todoItem, idx) => {
                  return <Todo key={idx} todoItem={todoItem} />
                })
              }
            </ul>)
              :
              <div>Noting to display</div>
          }
        </div>
        <div className="left-todo">
          {todoList.length > 0 ? `You have ${todoList.length} panding tasks.` : `You Don't have tasks. Please add some`}
        </div>
        <div className='delete-all'
                onClick={() => {clearAllTodos()}}>Clear all</div>
    </div>
  );
}

export default App;
