import './App.css'
import {useState} from 'react'

function App() {
  const [todo, setTodo] = useState('')
  const [todoList, setTodoList] = useState([])
  const addTodo = () => {
      if(todoList.length > 0){
        setTodoList([...todoList, todo])
        console.log(todoList)
      }
  }
  return (
    <div className="App">
      <div className="container">
        <h1 className="todo-title">
          TODO App
        </h1>
        <div className="add-todo">
          <input onChange={(event) => {setTodo(event.target.value)}}
                 className='input-add-todo'/>
          <button onClick={() => addTodo()}
                  className="material-symbols-rounded">
            add
          </button>
        </div>
        <div className="todos-list">
          {
            todoList.length >0 ? 
            (<ul>
              {
                todoList.map((todoItem, idx) => {
                  return <li key={idx}> {todoItem} </li>
                })
              }
            </ul>)
              :
              <div>Noting to display</div>
          }
        </div>
        <div className="left-todo">
          {todoList.length > 0 ? `You have ${todoList.length} panding tasks.` : ''}
        </div>
      </div>
    </div>
  );
}

export default App;
