import {Summary} from './components/Summary'
import {AddTodo} from './components/AddTodo'
import { useEffect } from 'react'

function App() {
  const {todos, todoAdded} = useSelector((state) => state)
  const dispatch = useDispatch()

  useEffect(() => {
    dispatch(fetchTodos())
  }, [dispatch])

useEffect(() => {
