import {useState} from 'react'

function App({todoList}) {
  const addTodo = () => {
      if(todo.trim().length !== 0 && !todoList.includes(todo)){
        setTodoList([...todoList, todo])
      }
  }
  const clearAllTodos = () => {
    setTodoList([])
  }
  return (
    <div className="todos-list">
      {
        todoList.length >0 ? 
        <ul>
          {
            todoList.map((todoItem, idx) => {
              return <li key={idx}> {todoItem} </li>
            })
          }
        </ul>
          :
          <div>Noting to display</div>
      }
    </div>
  );
}

export default App;
