import './App.css'
import {useState} from 'react'

function App() {
  const [todo, setTodo] = useState('')
  const [todoList, setTodoList] = useState([])
  const addTodo = () => {
      if(todo.trim().length !== 0 && !todoList.includes(todo)){
        setTodoList([...todoList, todo])
      }
  }
  const clearAllTodos = () => {
    setTodoList([])
  }
  return (
    <div className="App">
        <h1 className="todo-title">
          TODO App
        </h1>
        <div className="add-todo">
          <input onChange={(event) => {setTodo(event.target.value)}}
                 className='input-add-todo'/>
          <button onClick={() => addTodo()}
                  className="material-symbols-rounded">
            add
          </button>
        </div>
        <div className="todos-list">
          {
            todoList.length >0 ? 
            (<ul>
              {
                todoList.map((todoItem, idx) => {
                  return <li key={idx}> {todoItem} </li>
                })
              }
            </ul>)
              :
              <div>Noting to display</div>
          }
        </div>
        <div className="left-todo">
          {todoList.length > 0 ? `You have ${todoList.length} panding tasks.` : `You Don't have tasks. Please add some`}
        </div>
        <button className='delete-all'
                onClick={() => {clearAllTodos()}}>Clear all</button>
    </div>
  );
}

export default App;
