import './App.css'
import {useState} from 'react'

function App() {
  const [todoList, setTodoList] = useState(['1','2'])

  const displyTodoItems = () => {
    return <ul>
      {
        todoList.map((todoItem) => {
          return <li> {todoItem.title} </li>
        })
      }
    </ul>
  }
  return (
    <div className="App">
      <div className="container">
        <h1 className="todo-title">
          TODO App
        </h1>
        <div className="add-todo">
          <input className='input-add-todo'/>
          <button class="material-symbols-rounded">
            add
          </button>
        </div>
        <div className="todos-list">
          {
            todoList.length >0 ? 
              this.displayTodoItems()
              :
              <div>Noting to display</div>
          }
        </div>
      </div>
    </div>
  );
}

export default App;
